import{_ as l,c as e,a1 as i,o}from"./chunks/framework.clinZALv.js";const c=JSON.parse('{"title":"生命周期","description":"","frontmatter":{},"headers":[],"relativePath":"pages/文章/微信小程序/生命周期/index.md","filePath":"pages/文章/微信小程序/生命周期/index.md"}'),t={name:"pages/文章/微信小程序/生命周期/index.md"};function n(p,a,r,d,u,h){return o(),e("div",null,a[0]||(a[0]=[i('<h1 id="生命周期" tabindex="-1">生命周期 <a class="header-anchor" href="#生命周期" aria-label="Permalink to &quot;生命周期&quot;">​</a></h1><p>小程序的生命周期包括三个部分：</p><ul><li>应用生命周期</li><li>页面生命周期</li><li>组件生命周期</li></ul><h2 id="应用生命周期" tabindex="-1">应用生命周期 <a class="header-anchor" href="#应用生命周期" aria-label="Permalink to &quot;应用生命周期&quot;">​</a></h2><p>应用生命周期包括三个阶段：</p><ul><li>onLaunch</li></ul><p>onLaunch 是小程序初始化时触发的第一个生命周期函数，它在小程序启动时只调用一次。</p><ul><li>onShow</li></ul><p>onShow 是小程序从后台进入前台时触发的生命周期函数，它在小程序从后台进入前台时调用。</p><ul><li>onHide</li></ul><p>onHide 是小程序从前台进入后台时触发的生命周期函数，它在小程序从前台进入后台时调用。</p><h2 id="页面生命周期" tabindex="-1">页面生命周期 <a class="header-anchor" href="#页面生命周期" aria-label="Permalink to &quot;页面生命周期&quot;">​</a></h2><p>页面生命周期包括五个阶段：</p><ul><li>onLoad</li></ul><p>onLoad 是页面加载时触发的第一个生命周期函数，它在页面创建时调用。并且可以接受一个参数，即从前一个页面传递过来的参数。</p><ul><li>onShow</li></ul><p>页面显示/切入前台时触发</p><ul><li>onReady</li></ul><p>页面初次渲染完成时触发，可以和视图层进行交互。</p><ul><li>onHide</li></ul><p>页面隐藏/切入后台时触发</p><ul><li>onUnload</li></ul><p>页面卸载时触发，小程序从栈内移除页面时触发。</p><h2 id="组件生命周期" tabindex="-1">组件生命周期 <a class="header-anchor" href="#组件生命周期" aria-label="Permalink to &quot;组件生命周期&quot;">​</a></h2><p>组件生命周期包括三个阶段：</p><ul><li>created</li></ul><p>组件实例刚刚被创建时触发。</p><ul><li>attached</li></ul><p>组件实例被添加到页面时触发。</p><ul><li>detached</li></ul><p>组件实例被从页面移除时触发。</p><h2 id="执行顺序" tabindex="-1">执行顺序 <a class="header-anchor" href="#执行顺序" aria-label="Permalink to &quot;执行顺序&quot;">​</a></h2><img src="http://zhongxk.com/blogImg/小程序生命周期流程.png">',33)]))}const m=l(t,[["render",n]]);export{c as __pageData,m as default};
